import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';

function loadTweets(callback) {
  const xhr = new XMLHttpRequest();
  const method = 'GET';
  const url = "http://127.0.0.1:8000/api/tweets";
  const responseType = "json";
  xhr.responseType = responseType;
  xhr.open(method, url);
  xhr.onload = function() {
    callback(xhr.response, xhr.status)
  }
  xhr.onerror = function(e) {
    console.log(e)
    callback({"message": "The request was an error"}, 400)
  }
  xhr.send();

}

function likeBtn(tweet) {
  return "<button class='btn btn-primary btn-sm' onclick=handleTweetAction("
  +tweet.id+","+tweet.likes+",'like')>" + tweet.likes + " Likes</button>";
}

function Tweet(props) {
  const {tweet} = props
  const className = props.className ? props.className : 'col-10 mx-auto col-md-6'
  return <div className={className}>
    <p>{tweet.id} - {tweet.content}</p>
  </div>
}

function App() {
  const [tweets, setTweets] = useState([])

  useEffect(() => {
    //perform lookup in backend
    const myCallback = (response, status) => {
      if (status === 200)
        setTweets(response)
      else
        alert("There was an error!")
    }
    loadTweets(myCallback)

  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <div>
          {tweets.map((item, index)=>{
            return <Tweet tweet={item} className='my-5 py-5 border bg-white text-dark' key={`${index}-{tweet.id}`} />
          })}
        </div>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
